var doubleBits = require("double-bits");

var SMALLEST_DENORM = Math.pow(2, -1074);
var UINT_MAX = -1 >>> 0;
module.exports = nextafter;

function nextafter(x, y) {
  if (isNaN(x) || isNaN(y)) {
    return NaN;
  }

  if (x === y) {
    return x;
