    } else {
      return SMALLEST_DENORM;
    }
  }

  var hi = doubleBits.hi(x);
  var lo = doubleBits.lo(x);

  if (y > x === x > 0) {
    if (lo === UINT_MAX) {
      hi += 1;
      lo = 0;
    } else {
      lo += 1;
    }
